queries.yara_rule:
rule je_to_exit
{
    meta:
        description = "Matches the jump instruction leading to ExitProcess"
    strings:
        $je = { 74 ?? }
    condition:
        $je
}

rule SkipSandboxCheck {
    meta:
        description = "Skips the sandbox check by skipping the test and jump instructions"
    condition:
        85 C0 74 ?? // Test EAX, EAX followed by a conditional jump (JE)
}

rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip,count=0"

    strings:
        $pattern0 = { 85 C0 74 ?? }
        $pattern1 = { A1 88 85 03 01 85 C0 74 ?? }
        $pattern2 = { E8 ?? ?? ?? ?? A1 88 85 03 01 }

    condition:
        any of them
}

