llm_query.yara_rule:
rule PushCallSequence
{
    meta:
        description = "A sequence of push ebx followed by a call instruction"
    strings:
        $a = { 50 E8 ?? ?? ?? ?? }
    condition:
        $a
}

cape_analysis.yara_rule:
rule PushCallSequence
{
    meta:
        description = "A sequence of push ebx followed by a call instruction"
        cape_options = "bp0=$a+0,action0=skip,count=0"
    strings:
        $a = { 50 E8 ?? ?? ?? ?? }
    condition:
        $a
}

llm_query.yara_rule:
rule Bypass_Sample_Evolved
{
    meta:
        description = "Evasion bypass rule - evolved"
        cape_options = "bp0=$pattern0+0,action0=skip,bp1=$pattern1+0,action1=skip,bp2=$pattern2+0,action2=skip"

    strings:
        $pattern0 = { 85 C0 74 12 68 5B 00 00 00 }
        $pattern1 = { 85 C0 0F 84 33 FD FF FF }
        $pattern2 = { E8 ?? ?? ?? ?? }

    condition:
        all of them
}

