llm_query.yara_rule:
rule FunctionPrologue
{
    meta:
        description = "Function prologue used to set up stack frame"
    strings:
        $prologue = { 55 8B EC FF 75 08 }
    condition:
        $prologue
}

cape_analysis.yara_rule:
rule FunctionPrologue
{
    meta:
        description = "Function prologue used to set up stack frame"
        cape_options = "bp0=$prologue+0,action0=skip,count=0"
    strings:
        $prologue = { 55 8B EC FF 75 08 }
    condition:
        $prologue
}
