llm_query.yara_rule:
rule LoopControl
{
    meta:
        description = "Identifies loop control instructions"
    strings:
        $loop_control = { 40 3D 97 00 00 00 75 ?? }
    condition:
        all of them
}

rule TestAndJump
{
    meta:
        description = "Identifies test and jump instructions"
    strings:
        $test_jump = { 85 C0 0F 84 ?? ?? ?? ?? 8B 45 ?? }
    condition:
        all of them
}

rule CallExitProcess
{
    meta:
        description = "Identifies call to ExitProcess"
    strings:
        $call_exit = { 83 C4 04 E8 ?? ?? ?? ?? }
    condition:
        all of them
}

cape_analysis.yara_rule:
rule LoopControl
{
    meta:
        description = "Identifies loop control instructions"
        cape_options = "bp0=$loop_control+0,action0=skip,count=0"
    strings:
        $loop_control = { 40 3D 97 00 00 00 75 ?? }
    condition:
        all of them
}
